package com.vandersoncamp.bankslip.business;import com.vandersoncamp.bankslip.business.validate.BoletoValidate;import com.vandersoncamp.bankslip.model.Boleto;import com.vandersoncamp.bankslip.service.BoletoService;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import java.math.BigDecimal;import java.time.LocalDate;import java.time.temporal.ChronoUnit;import java.util.List;import java.util.UUID;@RequestScopedpublic class BoletoBusiness {    @Inject    private BoletoService service;    @Inject    private BoletoValidate boletoValidate;    private static final Integer CEM = 100;    public Boleto find(Long id) {        BigDecimal valorAtualizado;        Boleto boleto = service.find(id);        valorAtualizado = calculaValorBoleto(boleto);        boleto.setTotal_in_cents(valorAtualizado);        return boleto;    }    public List<Boleto> findAll(String filterField, String filterData, String order) {        return service.findAll(filterField, filterData, order);    }    public void create(Boleto boleto) {        boleto.setId(generateHashId());        boletoValidate.validate(boleto);        service.create(boleto);    }    public void update(String id, Boleto boleto) {        Boleto boletoSave = service.find(id);        boletoSave.setStatus(boleto.getStatus());        boletoValidate.validate(boletoSave);        service.update(boletoSave);    }    public void delete(Long id) {        service.delete(id);    }    private String generateHashId() {        String identificador;        do {            identificador = UUID.randomUUID().toString().substring(0, 36);        } while (service.existsIdentificador(identificador));        return identificador;    }    private BigDecimal calculaValorBoleto(Boleto boleto) {        BigDecimal valorMulta = BigDecimal.ZERO;        if (boleto.getDue_date().isBefore(LocalDate.now())) {            if (diferencaDias(boleto.getDue_date()) > 10) {                valorMulta = (boleto.getTotal_in_cents().multiply(BigDecimal.ONE.divide(BigDecimal.valueOf(CEM))));            } else {                valorMulta = (boleto.getTotal_in_cents().multiply(BigDecimal.valueOf(0.5).divide(BigDecimal.valueOf(CEM))));            }        }        return valorMulta;    }    private Long diferencaDias(LocalDate dataVencimento) {        return ChronoUnit.DAYS.between(dataVencimento, LocalDate.now());    }}