package com.vandersoncamp.bankslip.business.validate;import com.vandersoncamp.bankslip.model.Boleto;import com.vandersoncamp.bankslip.model.Status;import com.vandersoncamp.bankslip.util.exception.BusinessException;public class BoletoStatusValidate {    public void validate(Boleto boleto) {        if (boleto.getStatus().equals(Status.PAID)) {            throw new BusinessException("Boleto j치 est치 pago");        }        if (boleto.getStatus().equals(Status.CANCELED)) {            throw new BusinessException("Boleto j치 est치 cancelado");        }    }}