package com.vandersoncamp.bankslip.business.validate;import com.vandersoncamp.bankslip.model.Boleto;import com.vandersoncamp.bankslip.util.BusinessException;import java.text.ParseException;import java.text.SimpleDateFormat;public class BoletoValidate {    public void validate(Boleto boleto) {        if (!isDateValid(boleto.getDue_date().toString())) {            throw new BusinessException("Data do boleto est√° fora de formato");        }    }    private boolean isDateValid(String date) {        try {            new SimpleDateFormat("yyyy-MM-dd").parse(date);            return true;        } catch (ParseException e) {            return false;        }    }}