package com.vandersoncamp.bankslip.util;import javax.ejb.ApplicationException;import javax.xml.bind.annotation.XmlRootElement;import java.io.Serializable;@XmlRootElement(name = "BusinessException")@ApplicationException(rollback = true)public class BusinessException extends RuntimeException implements Serializable {    private final Integer codigo;    private final String[] messages;    public BusinessException(String message) {        super(message);        codigo = null;        messages = new String[0];    }    public BusinessException(Integer codigoErro, String messsage) {        super(messsage);        codigo = codigoErro;        messages = new String[0];    }}